    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        // TODO add your handling code here:

        try {
            //connect to db
            conect.connect();
            
            DefaultTableModel model = (DefaultTableModel) tblBorrowers.getModel();
            for(String[] row: conect.viewBorrowers()){
                model.addRow(row);
            }
            
        } catch (Exception ex) {
            System.out.println(ex.getMessage());
        }
    }//GEN-LAST:event_formWindowOpened

    private void btnDeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDeleteActionPerformed
        int borrowID;
        try {
            borrowID = Integer.parseInt(txtBorrowersID.getText().trim());
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Invalid Book ID.");
            return;
        }
        conect.deleteBorrower(borrowID);
        refreshBorowTable();
    }//GEN-LAST:event_btnDeleteActionPerformed

    private void btnViewSearchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnViewSearchActionPerformed
        String borrowerIDStr = txtBorrowersID.getText().trim();
        int borrowerID;
        try {
            borrowerID = Integer.parseInt(borrowerIDStr);
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Invalid Borrower ID.");
            return;
        }

        // Call the search method
        String[] borrower = conect.searchBorrowerById(borrowerID);
        if (borrower != null) {
            // Set the text fields with the search result
            txtName.setText(borrower[1]);
            txtSurname.setText(borrower[2]);
            txtEmail.setText(borrower[3]);
            txtphone.setText(borrower[4]);
            textAddress.setText(borrower[5]);
        } else {
            JOptionPane.showMessageDialog(this, "Borrower not found.");
        }
    }//GEN-LAST:event_btnViewSearchActionPerformed

    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
        //Get input
        String name = txtName.getText().trim();
        String surname = txtSurname.getText().trim();
        String email = txtEmail.getText().trim();
        String phone = txtphone.getText().trim();
        String address = textAddress.getText().trim();

        //Validate
        if (name.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Name is required.");
            return;
        }

        if (surname.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Surname is required.");
            return;
        }

        if (email.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Email is required.");
            return;
        }

        //Validate email
        if (!email.matches("^[\\w\\.-]+@[\\w\\.-]+\\.[a-zA-Z]{2,}$")) {
            JOptionPane.showMessageDialog(this, "Invalid email format.");
            return;
        }

        if (phone.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Phone number is required.");
            return;
        }

        // Validate phone
        if (!phone.matches("\\d{10}")) {
            JOptionPane.showMessageDialog(this, "Invalid phone number. It should be 10 digits.");
            return;
        }

        if (address.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Address is required.");
            return;
        }

        //add to db
        conect.addBorrower(name, surname, email, phone, address);
        refreshBorowTable();
        JOptionPane.showMessageDialog(this, "Borrower added successfully.");
        
        // Clear input 
        txtName.setText("");
        txtSurname.setText("");
        txtEmail.setText("");
        txtphone.setText("");
        textAddress.setText("");
    }//GEN-LAST:event_btnAddActionPerformed

    private void btnUpdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnUpdateActionPerformed
        //Get input
        String name = txtName.getText().trim();
        String surname = txtSurname.getText().trim();
        String email = txtEmail.getText().trim();
        String phone = txtphone.getText().trim();
        String address = textAddress.getText().trim();

        
        // Convert Book ID to an integer
        int borrowID;
        try {
            borrowID = Integer.parseInt(txtBorrowersID.getText().trim());
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Invalid Book ID.");
            return;
        }
        //Validate
        if (name.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Name is required.");
            return;
        }

        if (surname.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Surname is required.");
            return;
        }

        if (email.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Email is required.");
            return;
        }

        //Validate email
        if (!email.matches("^[\\w\\.-]+@[\\w\\.-]+\\.[a-zA-Z]{2,}$")) {
            JOptionPane.showMessageDialog(this, "Invalid email format.");
            return;
        }

        if (phone.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Phone number is required.");
            return;
        }

        // Validate phone
        if (!phone.matches("\\d{10}")) {
            JOptionPane.showMessageDialog(this, "Invalid phone number. It should be 10 digits.");
            return;
        }

        if (address.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Address is required.");
            return;
        }

        //add to db
        conect.updateBorrower(borrowID,name, surname, email, phone, address);
        refreshBorowTable();
        JOptionPane.showMessageDialog(this, "Borrower updated successfully.");

        // Clear input 
        txtName.setText("");
        txtSurname.setText("");
        txtEmail.setText("");
        txtphone.setText("");
        textAddress.setText("");
    }//GEN-LAST:event_btnUpdateActionPerformed

