    private void refreshBorowTable() {
    ArrayList<String[]> borrowers = conect.viewBorrowers();
    DefaultTableModel model = (DefaultTableModel) tblBorrowers.getModel();
    model.setRowCount(0); // Clear the table
    
    for (String[] borrow : borrowers) {
        model.addRow(borrow);
        }
    }
